CREATE TABLE IF NOT EXISTS "prefix_VectorStoreKnowledgeSourceHashes" (
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(),
    "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(),

    "source" TEXT NOT NULL,
    "hash" TEXT NOT NULL,
    "etag" TEXT,
    "lastModified" TEXT,
    "sizeBytes" BIGINT
);

CREATE UNIQUE INDEX IF NOT EXISTS "prefix_VectorStoreKnowledgeSourceHashes_source_idx" ON "prefix_VectorStoreKnowledgeSourceHashes" ("source");

ALTER TABLE "prefix_VectorStoreKnowledgeSourceHashes" ENABLE ROW LEVEL SECURITY;
